```cpp
int n,m;
int a[N],fa[N];
vector<int>v[N];
vector<int>ans;
queue<int>q;
queue<int>q2;
void bfs(int x)
{
	q.push(x);
	if(a[x]==-1) return;
	while(q.size())
	{
		int h1=0;
		int len=q.size();
		for(int i=1;i<=len;i++)//记录当前层的下一层有没有 1，淘汰下一层所有的 0 节点
		{
			int x=q.front();q.pop();
			for(int i=0;i<v[x].size();i++) if(a[v[x][i]]==1&&v[x][i]!=fa[x]) h1++;
			q2.push(x);
		}
		for(int i=1;i<=len;i++)
		{
			int x=q2.front();q2.pop();
			// caculate ans
			if(a[x]==1) ans.push_back(x);
			// go down
			int h2=0;
			for(int i=0;i<v[x].size();i++)
			{

				int y=v[x][i];
				if(y==fa[x]) continue;
				if(a[y]==-1) continue;
				if(h1&&(a[y]!=1)) continue;
				fa[y]=x; q.push(y);
			}
		}
		if(q.size()&&h1) ans.clear();
	}
}
int vis[N],pos=0;
void dfs(int x,int f)
{
	if(pos) return;
	if(vis[x])
	{
		pos=x;
		return;
	}
	for(int i=0;i<v[x].size();i++)
	{
		int y=v[x][i];
		if(y==f) continue;
		dfs(y,x);
	}
}
