	for(int i=1;i<=n;i++) sort(v[i].begin(),v[i].end());
	bfs(1);
	for(int i=0;i<ans.size();i++) vis[ans[i]]=1;
	dfs(1,0);
	if(!pos) return;
	while(fa[pos]) s.push(pos),pos=fa[pos];s.push(pos);
	while(s.size()) wt(s.top(),3),s.pop();
}

