```cpp
#include<bits/stdc++.h>
using namespace std;
#define ll long long
namespace IO{//by cyffff

}
#define vec vector<int>
const int N=2.5e5+10,sq=512,B=1e9+10;
const ll INF=1e18;
int n,q,px[N],py[N];
ll ans[N];
vector<int>use[N];
struct Query{
	int l,id;
};
vector<Query>qr[N];
inline void push(int u,int v){
	if(u>v) swap(u,v);
	use[v].emplace_back(u);
//	printf("use %d %d\n",u,v);
}
inline ll sqr(int x){
	return 1ll*x*x;
}
inline ll dis(int x,int y){
	return sqr(px[x]-px[y])+sqr(py[x]-py[y]);
}
struct SQR{
	ll mn1[N],mn2[N];
	int bel[N],br[N];
	inline void init(){
		for(int i=1,k=1;i<=n;i+=sq,k++){
			for(int j=i;j<=min(i+sq-1,n);j++)
				bel[j]=k,mn1[j]=mn2[j]=INF;
			br[k]=min(i+sq-1,n);
		}
	}
	inline void add(int p,ll v){
		mn1[p]=min(mn1[p],v);
		mn2[bel[p]]=min(mn2[bel[p]],v);
	}
	inline ll query(int p){
		ll ans=INF;
		for(int i=p;i<=br[bel[p]];i++)
			ans=min(ans,mn1[i]);
		for(int i=bel[p]+1;i<=bel[n];i++)
			ans=min(ans,mn2[i]);
		return ans;
	}
}T;
template<class P,class Q>
class HashTable{
	static const int mod=5e5+9,L=mod+10;
	int head[L],nxt[N],siz;
	P val[N];
	Q sav[N];
    public:
	inline void clear() {
		for(int i=1;i<=siz;i++){
			head[val[i]%mod]=0,nxt[i]=val[i]=0;
			Q tmp;
			swap(sav[i],tmp);
		}
		siz=0;
	}
	inline void insert(P y){
		val[++siz]=y;
		nxt[siz]=head[y%mod];
		head[y%mod]=siz;
	}
	inline Q &operator[](const P y) {
		int x=y%mod;
		for(int i=head[x];i;i=nxt[i])
			if(val[i]==y)
				return sav[i];
		insert(y);
		return sav[siz];
	}
	inline bool count(const P y) {
		int x=y%mod;
		for(int i=head[x];i;i=nxt[i])
			if(val[i]==y)
				return 1;
		return 0;
	}
};
HashTable<ll,vec>mp;
inline ll mpr(int a,int b){
	return 1ll*a*B+b;
}
int main(){
	n=read(),q=read();
	for(int i=1;i<=n;i++)
		px[i]=read(),py[i]=read(); 
	T.init();
	for(int i=1;i<=q;i++){
		int l=read(),r=read();
		qr[r].push_back({l,i});
	}
	for(int s=0;s<=28;s+=2){
		mp.clear();
		for(int i=1;i<=n;i++){
			int x=px[i],y=py[i];
			int bx=x>>s,by=y>>s;
			for(int dx=-1;dx<=1;dx++)
				for(int dy=-1;dy<=1;dy++){
					if(bx+dx<0||by+dy<0) continue;
					if(!mp.count(mpr(bx+dx,by+dy))) continue;
					vec tmp,tmq=mp[mpr(bx+dx,by+dy)];
					tmp.clear();
					for(auto p:tmq){
						push(p,i);
						if(dis(p,i)>=(1ll<<(s*2-4))) tmp.push_back(p);
					}
					mp[mpr(bx+dx,by+dy)]=tmp;
				}
			mp[mpr(bx,by)].emplace_back(i);
		}
	}
	for(int i=1;i<=n;i++){
		sort(use[i].begin(),use[i].end());
		use[i].resize(unique(use[i].begin(),use[i].end())-use[i].begin());
//		for(auto j:use[i])
//			printf("use %d %d %lld\n",j,i,dis(i,j));
	}
	for(int i=1;i<=n;i++){
		for(auto t:use[i])
			T.add(t,dis(t,i));
		for(auto t:qr[i])
			ans[t.id]=T.query(t.l);
	}
	for(int i=1;i<=q;i++)
		write(ans[i]),putc('\n');
	flush();
}
