int main(){
	ios::sync_with_stdio(0);
	cin.tie(0);
	cout.tie(0);
	cin>>t;
	for(;t--;){
		cin>>n;
		if(n%2==1)cout<<"Yes\n";
		else cout<<"No\n";
	}
	return 0;
}
```
