		res=get(n,2),tot=3;
	else
		res=1,tot=k;
	vis[1]=1;
	while(1){
		int x=get(n,tot);
		if(x==1)
			break;
		for(int i=2;i<=x;++i){
			int val=qpow(i,tot);
			if(!vis.count(val)){
				if(k==2&&check(val))
					continue;
				vis[val]=1;
				++res;
			}
		}
		++tot;
	}
