void solve()
{
	int T=read();
	while(T--)
	{
		string s;
		int x=read();
		while(x)
		{
			s+=(x%3+'0');
			x/=3;//s存下三进制数，这里其实是反着存的，不过没有影响。
		}
//		cout<<s<<endl;
		int len=s.size(),ans=1;//累成器初始化。
		if(s[0]=='2') ans=0;//不合法时直接ans=0，0乘任何数等于0。
		for(int i=1;i<len;i++)
		{
			if(s[i]=='1') ans*=2;//结论。
		}
		printf("%lld\n",ans);
	}
}
