using namespace std;
int t,x,ans;
signed main(){
	scanf("%lld",&t);
	while(t--){
		scanf("%lld",&x);
		if(x%3==2)
			printf("0\n");
		else{
			ans=1,x/=3;
			while(x){
				if(x%3==1)
					ans<<=1;
				x/=3;
			}
			printf("%lld\n",ans);
		} 
	}
	return 0;
}
```

