    {
        pii res={val,u};
        for(int i=head[u];i;i=e[i].nxt)
        {
            int v=e[i].to;
            if(v==fa) continue;
            b[v]=(e[i].w==1&&e[i^1].w==1);
            ans+=(e[i^1].w==1);
            pii t=dfs3(v,u,val+e[i].w-e[i^1].w-b[v]);
