			x = y = 0;
			for (int i = 1; i <= n; i++) a[i] >> p & 1 ? ++x : ++y;
			if (n & 1) for (int i = 1; i <= n; i++) a[i] ^= (x & 1) << p;
			else if (x & 1) for (int i = 1; i <= n; i++) a[i] ^= (k & 1) << p;
