    if(P==0) sum+=a[G]*2;
    else if(P==1) sum+=a[G],J++;
    for(int i=0;i<3;i++) LHQ(G+1,i,J,sum);
}
void HG(int G,int P,int J,long long sum){
    if(sum>m||J>ans) return;
    if(sum==m){
        ans=min(ans,PII[sum]+J);
